        i0.ɵɵelementStart(445, "p", 81);
        i0.ɵɵtext(446, " \u0642\u062F \u0646\u0642\u0648\u0645 \u0623\u064A\u0636\u0627\u064B \u0628\u062C\u0645\u0639 \u0645\u0639\u0644\u0648\u0645\u0627\u062A \u0645\u0639\u064A\u0646\u0629 \u0639\u0628\u0631 \u062A\u0642\u0646\u064A\u0627\u062A \u0645\u062B\u0644 \u0645\u0644\u0641\u0627\u062A \u062A\u0639\u0631\u064A\u0641 \u0627\u0644\u0627\u0631\u062A\u0628\u0627\u0637 \u00ABCookies\u00BB\u060C \u0648\u0635\u0648\u0631 \u0627\u0644\u0640 \u00ABPixels\u00BB\u060C \u0648\u0627\u0644\u062A\u062E\u0632\u064A\u0646 \u0627\u0644\u0645\u062D\u0644\u064A (\u0645\u062B\u0644 \u0645\u0633\u062A\u0639\u0631\u0636\u0643 \u0623\u0648 \u062C\u0647\u0627\u0632\u0643). \u0645\u0644\u0641\u0627\u062A \u062A\u0639\u0631\u064A\u0641 \u0627\u0644\u0627\u0631\u062A\u0628\u0627\u0637 \u00ABCookies\u00BB \u0647\u064A \u0646\u0635\u0648\u0635 \u0635\u063A\u064A\u0631\u0629 \u062A\u062E\u0632\u0646 \u0645\u0639\u0644\u0648\u0645\u0627\u062A \u062D\u0648\u0644 \u062C\u0647\u0627\u0632 \u0627\u0644\u0643\u0645\u0628\u064A\u0648\u062A\u0631 \u0623\u0648 \u0627\u0644\u0647\u0627\u062A\u0641 \u0627\u0644\u062C\u0648\u0627\u0644 \u0627\u0644\u062E\u0627\u0635 \u0628\u0643 \u0623\u0648 \u0627\u0644\u062C\u0647\u0627\u0632 \u0627\u0644\u0622\u062E\u0631 \u0645\u062D\u0644\u064A\u0627\u064B. \u0648\u0635\u0648\u0631 \u0627\u0644\u0640 \u00ABPixels\u00BB \u0647\u064A \u0635\u0648\u0631 \u0635\u063A\u064A\u0631\u0629 \u062A\u064F\u0639\u062F\u064F\u0651 \u062C\u0632\u0621\u0627\u064B \u0645\u0646 \u0631\u0645\u0648\u0632 \u0639\u0644\u0649 \u0635\u0641\u062D\u0627\u062A \u0627\u0644\u0648\u064A\u0628 \u062A\u0642\u0648\u0645 \u0628\u0623\u062F\u0627\u0621 \u0648\u0638\u0627\u0626\u0641 \u0645\u062B\u0644 \u0627\u0644\u0633\u0645\u0627\u062D \u0644\u062E\u0627\u062F\u0645 \u0622\u062E\u0631 \u0628\u0642\u064A\u0627\u0633 \u0646\u0633\u0628\u0629 \u0627\u0633\u062A\u0639\u0631\u0627\u0636 \u0635\u0641\u062D\u0629 \u0627\u0644\u0648\u064A\u0628 \u0648\u063A\u0627\u0644\u0628\u0627\u064B \u0645\u06