        i0.ɵɵelementStart(450, "p", 81);
        i0.ɵɵtext(451, " \u0644\u062A\u0642\u062F\u064A\u0645 \u0627\u0644\u062E\u062F\u0645\u0627\u062A \u0644\u0643 \u0642\u062F \u0646\u0642\u0648\u0645 \u0628\u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0627\u0644\u0645\u0639\u0644\u0648\u0645\u0627\u062A \u0627\u0644\u062A\u064A \u0646\u062C\u0645\u0639\u0647\u0627 \u0648\u0646\u0633\u062A\u0636\u064A\u0641\u0647\u0627 \u0644\u0644\u0623\u063A\u0631\u0627\u0636 \u0627\u0644\u0639\u0627\u0645\u0629 \u0627\u0644\u062A\u0627\u0644\u064A\u0629: (1) \u062A\u0642\u062F\u064A\u0645 \u062E\u062F\u0645\u0627\u062A \u0627\u0644\u0639\u0645\u064A\u0644\u060C \u0628\u0645\u0627 \u0641\u064A \u0630\u0644\u0643 \u0625\u0646\u0634\u0627\u0621 \u0648\u0625\u062F\u0627\u0631\u0629 \u062D\u0633\u0627\u0628\u0627\u062A \u0627\u0644\u0645\u0633\u062A\u062E\u062F\u0645\u060C \u0648\u062D\u0644 \u0627\u0644\u0635\u0639\u0648\u0628\u0627\u062A \u0627\u0644\u062A\u0642\u0646\u064A\u0629 \u0648\u062A\u0645\u0643\u064A\u0646 \u0627\u0644\u0645\u0632\u0627\u064A\u0627\u061B (2) \u062A\u062E\u0635\u064A\u0635 \u0627\u0644\u0639\u0631\u0648\u0636 \u0648\u0627\u0644\u062E\u0628\u0631\u0627\u062A\u060C \u0628\u0645\u0627 \u0641\u064A \u0630\u0644\u0643 \u0627\u0644\u0625\u0639\u0644\u0627\u0646 \u0639\u0644\u0649 \u0627\u0644\u0645\u0648\u0627\u0642\u0639 \u0627\u0644\u062A\u0627\u0628\u0639\u0629 \u0644\u0646\u0627 \u0623\u0648 \u0644\u0644\u063A\u064A\u0631\u061B (3) \u0627\u0644\u0631\u0642\u0627\u0628\u0629 \u0639\u0644\u0649 \u0646\u0634\u0627\u0637\u0627\u062A \u0627\u0644\u0645\u0633\u062A\u062E\u062F\u0645 \u0627\u0644\u0639\u0627\u0645\u0629 \u0648\u0627\u0644\u0641\u0631\u062F\u064A\u0629 \u0645\u062B\u0644 \u0639\u0645\u0644\u064A\u0627\u062A \u0628\u062D\u062B \u0644\u0648\u062D\u0629 \u0627\u0644\u0645\u0641\u0627\u062A\u064A\u062D \u0648\u0627\u0644\u0645\u0646\u0634\u0648\u0631\u0627\u062A \u0648\u0646\u0634\u0627\u0637\u0627\u062A \u0627\u0644\u0645\u0639\u0627\u0645\u0644\u0627\u062A\u060C \u0648\u0625\u062F\u0627\u0631\u062