        i0.ɵɵelementStart(455, "p", 81);
        i0.ɵɵtext(456, " \u0642\u062F \u0646\u0642\u0648\u0645 \u0628\u0645\u0634\u0627\u0631\u0643\u0629 \u0645\u0639\u0644\u0648\u0645\u0627\u062A \u0646\u0642\u0648\u0645 \u0628\u062C\u0645\u0639\u0647\u0627 \u0645\u0639 \u0627\u0644\u062C\u0647\u0627\u062A \u0627\u0644\u062A\u0627\u0628\u0639\u0629 \u0644\u0646\u0627 (\u0634\u0631\u0643\u0627\u062A \u062A\u062D\u062A \u0645\u0644\u0643\u064A\u062A\u0646\u0627 \u0623\u0648 \u062A\u062D\u0643\u0645\u0646\u0627 \u0627\u0644\u0645\u0634\u062A\u0631\u0643) \u062A\u0642\u0639 \u0641\u064A \u0623\u064A \u0628\u0644\u062F \u0622\u062E\u0631. \u0648\u064A\u062C\u0648\u0632 \u0644\u062A\u0644\u0643 \u0627\u0644\u0634\u0631\u0643\u0627\u062A \u0641\u0642\u0637 \u0623\u0648 \u062A\u0642\u0648\u0645 \u0628\u0645\u0639\u0627\u0644\u062C\u0629 \u0648\u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0647\u0630\u0647 \u0627\u0644\u0645\u0639\u0644\u0648\u0645\u0627\u062A \u0644\u0645\u0633\u0627\u0639\u062F\u062A\u0646\u0627 \u0641\u064A \u0627\u0644\u0623\u063A\u0631\u0627\u0636 \u0627\u0644\u0645\u0628\u064A\u0646\u0629 \u0623\u0639\u0644\u0627\u0647 \u0648\u062A\u0644\u062A\u0632\u0645 \u0628\u0627\u062D\u062A\u0631\u0627\u0645 \u0633\u064A\u0627\u0633\u0629 \u0627\u0644\u062E\u0635\u0648\u0635\u064A\u0629 \u0647\u0630\u0647. \u0648\u0644\u0627 \u0646\u0642\u0648\u0645 \u0628\u062A\u0634\u0627\u0631\u0643 \u0645\u0639\u0644\u0648\u0645\u0627\u062A \u0634\u062E\u0635\u064A\u0629 \u062D\u0648\u0644 \u0645\u0633\u062A\u062E\u062F\u0645\u064A\u0646\u0627 \u0645\u0639 \u0623\u0637\u0631\u0627\u0641 \u0623\u062E\u0631\u0649 \u063A\u064A\u0631 \u062A\u0627\u0628\u0639\u0629 \u0644\u0646\u0627 \u0645\u0627 \u0644\u0645 \u0646\u062D\u0632 \u0639\u0644\u0649 \u0625\u0630\u0646 \u062E\u0627\u0635 \u0645\u0646 \u0645\u0633\u062A\u062E\u062F\u0645\u064A\u0646\u0627 \u0644\u0644\u0642\u064A\u0627\u0645 \u0628\u0630\u0644\u0643\u060C \u0623\u0648 \u0641\u064A \u0627\u0644\u062D\u0627\u0644\u0627\u062A \u0627\u0644\u062A\u0627\u0644\u064A\u0629: \u0642\u062F \u0646\u0633\u062A\u062E\u062F\u0645 \u0645\u0632\u0648\u062F\u064A \u062E\u062F\u0645\u0627\u062A \u0622\u062E\u0631\u064A\u0646 \u0644\u0644\u0645\u0633\u0627\u0639\u062F\u0629 \u0639\u0644\u0649 \u0639\u0631\u0636 \u0645\u062E\u062A\u0644\u0641 \u062C\u0648\u0627\u0646\u0628 \u062E\u062F\u0645\u062A\u0646\u0627\u060C \u0648\u0641\u064A \u0643\u0644 \u062D\u0627\u0644\u0629 \u0641\u0625\u0646\u0647\u0645 \u0644\u0627 \u064A\u0645\u062A\u0644\u0643\u0648\u0646 \u0627\u0644\u0635\u0644\u0627\u062D\u064A\u0629 \u0644\u0644\u0642\u064A\u0627\u0645 \u0628\u0634\u0643\u0644 \u0645\u0633\u062A\u0642\u0644 \u0628\u0627\u0633\u062A\u062E\u062F\u0627\u0645 \u0623\u064A\u0629 \u0645\u0639\u0644\u0648\u0645\u0627\u062A \u0634\u062E\u0635\u064A\u0629 \u0642\u062F \u064A\u062D\u0648\u0632\u0648\u0646\u0647\u0627 \u0628\u0627\u0644\u0646\u064A\u0627\u0628\u0629 \u0639\u0646\u0647\u0627\u060C \u0648\u0628\u0645\u0627 \u064A\u062E\u0636\u0639 \u0644\u0634\u0631\u0648\u0637 \u0633\u064A\u0627\u0633\u0629 \u0627\u0644\u062E\u0635\u0648\u0635\u064A\u0629 \u0647\u0630\u0647. \u0648\u0646\u062D\u062A\u0641\u0638 \u0628\u062D\u0642\u0646\u0627 \u0641\u064A \u0645\u0634\u0627\u0631\u0643\u0629 \u0645\u0639\u0644\u0648\u0645\u0627\u062A \u0645\u0639 \u0633\u0644\u0637\u0627\u062A \u0639\u0627\u0645\u0629 \u0623\u0648 \u062E\u0627\u0635\u0629 \u0628\u0645\u0627 \u064A\u0645\u0643\u0646\u0646\u0627 \u0645\u0646 \u0645\u0643\u0627\u0641\u062D\u0629 \u0627\u0644\u063A\u0634 \u0648\u0627\u0644\u0627\u0646\u062A\u0647\u0627\u0643\u0627\u062A \u0639\u0644\u0649 \u0634\u0628\u0643\u062A\u0646\u0627\u060C \u0648\u0627\u0644\u062A\u062D\u0642\u0642 \u0645\u0646 \u0639\u0645\u0644\u064A\u0627\u062A \u0645\u062E\u0627\u0644\u0641\u0629 \u0627\u0644\u0642\u0627\u0646\u0648\u0646 \u0627\u0644\u0645\u0634\u0643\u0648\u0643 \u0628\u0623\u0645\u0631\u0647\u0627\u060C \u0623\u0648 \u0645\u0643\u0627\u0641\u062D\u0629 \u0627\u0644\u0625\u062E\u0644\u0627\u0644\u0627\u062A \u0627\u0644\u0623\u06