        // console.log("resolver is work ",id,type)
        if (type == "fodder") {
            arr = localStorage.getItem('stockId');
            return this.statistics.StatisicsMembersFodder(id, type, '', '', '0', '').pipe(catchError((e) => {
                this.toster.showFail(e.error.error);
                this.alertService.error(e.error.error);
                return EMPTY;
            }));
        }
        else {
            return this.statistics.StatisicsMembersLocal(id, type, '', '', '').pipe(catchError((e) => {
                // this.router.navigate([""]);
                this.toster.showFail(e.error.error);
                this.alertService.error(e.error.error);
                return EMPTY;
            }));
