            shortDate = date.getFullYear() + '-0' + (date.getMonth() + 1) + '-' + date.getDate();
        }
        else {
            shortDate = date.getFullYear() + '-' + (date.getMonth() + 1) + '-' + date.getDate();
        }
        return shortDate;
    }
}
